package com.draganddrop;import com.utilities.Driver;import com.utilities.ReusableMethods;import org.openqa.selenium.By;import org.openqa.selenium.Keys;import org.openqa.selenium.WebElement;import org.openqa.selenium.interactions.Actions;import org.testng.Assert;import org.testng.annotations.Test;public class DragAndDropTest {    Actions actions;    @Test    public void dragAndDropTest() {        String url = "http://dhtmlgoodies.com/scripts/drag-drop-quiz/drag-drop-quiz-d2.html";        Driver.getDriver().get(url);        actions = new Actions(Driver.getDriver());        actions.sendKeys(Keys.PAGE_DOWN).perform();        for(int i = 1; i<16; i++) {            String capitalXpath = "//div[@id='a" + i + "']";            WebElement sourceCapitalBlock = Driver.getDriver().findElement(By.xpath(capitalXpath));            ReusableMethods.waitFor(1);            actions.clickAndHold(sourceCapitalBlock).perform();            ReusableMethods.waitFor(1);            String countryXpath = "//div[@id='q" + i + "']";            WebElement targetCountryBlock = Driver.getDriver().findElement(By.xpath(countryXpath));            WebElement lastTarget = targetCountryBlock.findElement(By.xpath("following::div"));            actions.moveToElement(lastTarget).release().perform();            ReusableMethods.waitFor(1);            Assert.assertEquals(lastTarget.getText(),sourceCapitalBlock.getText());        }    }}