package com.draganddrop;import com.utilities.Driver;import com.utilities.ReusableMethods;import org.openqa.selenium.By;import org.openqa.selenium.WebElement;import org.openqa.selenium.interactions.Actions;import org.testng.annotations.Test;import java.util.List;public class DragAndDropTest2 {    Actions actions;    @Test    public void dragAndDrop() {        String url = "http://dhtmlgoodies.com/scripts/drag-drop-nodes-quiz/drag-drop-nodes-quiz.html";        Driver.getDriver().get(url);        actions = new Actions(Driver.getDriver());        for (int i =1; i<8; i++) {            List<WebElement> objectBox = Driver.getDriver().findElements(By.xpath("//li[@groupid='box" + i + "']"));            for (WebElement w : objectBox) {                ReusableMethods.waitFor(2);                WebElement target = Driver.getDriver().findElement(By.xpath("//ul[@id='box" + i +"']"));                actions.clickAndHold(w).perform();                ReusableMethods.waitFor(2);                actions.moveToElement(target).release().perform();            }        }    }}