package com.draganddrop;import com.utilities.Driver;import com.utilities.ReusableMethods;import org.openqa.selenium.By;import org.openqa.selenium.WebElement;import org.openqa.selenium.interactions.Actions;import org.testng.annotations.Test;public class DragAndDropTest3 {    Actions actions;    @Test    public void dragAndDrop() {        String url = "http://dhtmlgoodies.com/scripts/drag-drop-nodes/drag-drop-nodes.html";        Driver.getDriver().get(url);        actions = new Actions(Driver.getDriver());        int k = 1;        for (int i = 1; i < 22; i++) {            WebElement first4Students = Driver.getDriver().findElement(By.xpath("//li[@id='node" + i + "']"));            WebElement teamA = Driver.getDriver().findElement(By.xpath("//ul[@id='box"+ k + "']"));            ReusableMethods.waitFor(1);            actions.clickAndHold(first4Students).perform();            ReusableMethods.waitFor(1);            actions.moveToElement(teamA).release().perform();            if(i==4 || i==8 || i==11 ) k++;        }    }}